import React, { useState } from 'react';

	

const App = () => {

	const BgColor = 'red';
	const [bg, setNewBg] = useState(BgColor);

	const [text, setText] = useState('Click Here');

	const bgChange = () => {
		let newBg = 'yellow';
		setNewBg(newBg);

		setText('Haye!!');
	};


	return (
		<div style={{backgroundColor:bg }}>
			<button onClick={bgChange}>{text}</button>
		</div>
		);
};

export default App;
